import React from "react";
import { Formik } from "formik";
import * as Yup from "yup";

const validateObj = Yup.object().shape({
  title: Yup.string()
    .min(3, "حداقل ۳ کاراکتر وارد کنید")
    .max(50, "بیشتر از ۵۰ کاراکتر مجاز نیست")
    .required("عنوان تسک را وارد کنید"),
  description: Yup.string()
    .min(10, "حداقل ۱۰ کاراکتر وارد کنید")
    .max(200, "بیشتر از ۲۰۰ کاراکتر مجاز نیست")
    .required("توضیحات تسک را وارد کنید"),
});

const initialValues = {
  title: "",
  description: "",
};

const TaskForm = (props) => {
  const { handleSubmit, renderOptions } = props;

  return (
    <Formik
      initialValues={initialValues}
      onSubmit={handleSubmit}
      validationSchema={validateObj}
    >
      {renderOptions && renderOptions()}
    </Formik>
  );
};

export default TaskForm;
