import React from "react";

import styled from "styled-components";
import TodoOption from "./TodoOption";

const TodoItem = (props) => {
  const { title, description, read } = props;

  return (
    <TodoListItem read={read}>
      {!read && <i className="las la-exclamation-circle unReadMark"></i>}
      <div className="todoListContent">
        <h2>{title}</h2>
        <p>{description}</p>
      </div>
      <div className="todoListOptions">
        {read ? (
          <TodoOption className="las la-envelope-open" title="Mark as unread" />
        ) : (
          <TodoOption className="las la-envelope" title="Mark as read" />
        )}
      </div>
    </TodoListItem>
  );
};

const TodoListItem = styled.li((props) => {
  const { read } = props;

  return {
    borderBottom: "1px solid #99999933",
    position: "relative",
    padding: "15px 0",
    color: "#555",
    fontSize: ".9rem",
    fontWeight: read ? 100 : "bold",
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
    "&:last-of-type": {
      borderWidth: 0,
    },
    ".unReadMark": {
      color: "#d84f4d",
      position: "absolute",
      top: 15,
      right: 15,
    },
    ".todoListContent": {
      h2: {
        fontWeight: read ? 500 : "bold",
      },
    },
  };
});

export default TodoItem;
