import React from "react";

import styled from "styled-components";

const tabs = ["All", "Todo", "Completed"];

const MainBox = (props) => {
  const { empty, children, renderEmpty, title, renderBottom } = props;

  return (
    <>
      <MainBoxContainer>
        <h1>{title}</h1>
        <ul>
          {tabs.map((item, index) => {
            const key = `todoListTabItem${index}${item}`;
            return <li key={key}>{item}</li>;
          })}
        </ul>
        <div className="indexTodoBox">
          {empty ? <>{renderEmpty && renderEmpty()}</> : children}
        </div>
        {renderBottom && renderBottom()}
      </MainBoxContainer>
    </>
  );
};

const MainBoxContainer = styled.main(() => {
  return {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    flexDirection: "column",
    minHeight: "100vh",
    h1: {
      fontSize: "1.9rem",
      color: "#333",
      padding: "0 30px",
      marginTop: 30,
      marginBottom: 10,
    },
    ul: {
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
      li: {
        margin: "0 10px",
      },
    },
    ".indexTodoBox": {
      background: "#fff",
      padding: 30,
      width: "100%",
      maxWidth: 500,
      margin: 30,
      borderRadius: 4,
      boxShadow: "2px 2px 5px 1px #999",
    },
  };
});

export default MainBox;
