import React from "react";

import styled from "styled-components";

const Input = (props) => {
  const { onBlur, onChange, value, err, hasErr, id, label } = props;

  return (
    <TaskInput hasErr={hasErr}>
      <label htmlFor={id}>{hasErr ? err : label}</label>
      <input
        type="text"
        id={id}
        onChange={onChange}
        value={value}
        onBlur={onBlur}
      />
    </TaskInput>
  );
};

const TaskInput = styled.div((props) => {
  const { hasErr } = props;

  return {
    marginBottom: 15,
    label: {
      width: "100%",
      fontSize: ".85rem",
      color: "#555",
    },
    input: {
      width: "100%",
      height: 40,
      borderRadius: 4,
      border: "1px solid #99999944",
    },
  };
});

export default Input;
