import React from "react";

import styled from "styled-components";

const TodoItem = (props) => {
  const { title, description, read } = props;

  return (
    <TodoListItem read={read}>
      {!read && <i className="las la-exclamation-circle unReadMark"></i>}
      <div className="todoListContent">
        <h2>{title}</h2>
        <p>{description}</p>
      </div>
    </TodoListItem>
  );
};

const TodoListItem = styled.li((props) => {
  const { read } = props;

  return {
    borderBottom: "1px solid #99999933",
    position: "relative",
    padding: "15px 0",
    cursor: "pointer",
    color: "#555",
    fontSize: ".9rem",
    fontWeight: read ? 100 : "bold",
    "&:last-of-type": {
      borderWidth: 0,
    },
    "&:hover": {
      backgroundColor: "#99999911",
    },
    ".unReadMark": {
      color: "#d84f4d",
      position: "absolute",
      top: 15,
      right: 15,
    },
    ".todoListContent": {
      h2: {
        fontWeight: read ? 500 : "bold",
      },
    },
  };
});

export default TodoItem;
