import React from "react";
import { Formik } from "formik";
import * as Yup from "yup";

const validateTitle = Yup.object().shape({
  code: Yup.string().required("پر کردن کادر کد اجباری می‌باشد"),
  password: Yup.string()
    .min(3, "حداقل ۳ کاراکتر وارد کنید")
    .max(50, "بیشتر از ۵۰ کاراکتر مجاز نیست")
    .required("عنوان تسک را وارد کنید"),
});

const validateDescription = Yup.object().shape({
  code: Yup.string().required("پر کردن کادر کد اجباری می‌باشد"),
  password: Yup.string()
    .min(8, "حداقل ۸ کاراکتر وارد کنید")
    .max(40, "بیشتر از ۴۰ کاراکتر مجاز نیست")
    .required("رمز عبور خود را وارد کنید"),
  confirmPassword: Yup.string()
    .oneOf([Yup.ref("password"), null], "رمزعبور مطابقت ندارد")
    .required("تکرار رمزعبور اجباری می‌باشد"),
});

const TaskForm = () => {
  return <div>task form</div>;
};

export default TaskForm;
