import React from "react";
import { Formik } from "formik";
import * as Yup from "yup";
import Input from "./Input";

import styled from "styled-components";

const validateObj = Yup.object().shape({
  title: Yup.string()
    .min(3, "حداقل ۳ کاراکتر وارد کنید")
    .max(50, "بیشتر از ۵۰ کاراکتر مجاز نیست")
    .required("عنوان تسک را وارد کنید"),
  description: Yup.string()
    .min(10, "حداقل ۱۰ کاراکتر وارد کنید")
    .max(200, "بیشتر از ۲۰۰ کاراکتر مجاز نیست")
    .required("توضیحات تسک را وارد کنید"),
});

const initialValues = {
  title: "",
  description: "",
};

const TaskForm = (props) => {
  const { handleSubmit, renderOptions } = props;

  return (
    <Formik
      initialValues={initialValues}
      onSubmit={handleSubmit}
      validationSchema={validateObj}
    >
      {(formProps) => {
        const { values, errors, touched, handleChange, handleBlur } = formProps;

        const titleErr = errors.title && touched.title;
        const descriptionErr = errors.description && touched.description;

        return (
          <TaskFormContainer onSubmit={handleSubmit} autoComplete="off">
            {renderOptions && renderOptions()}
            <Input
              label={"Title"}
              onChange={handleChange}
              handleBlur={handleBlur}
              value={values.title}
              err={errors.title}
              hasErr={titleErr}
              id={"addTaskInputTitle"}
            />
            <Input
              label={"Description"}
              onChange={handleChange}
              handleBlur={handleBlur}
              value={values.description}
              err={errors.description}
              hasErr={descriptionErr}
              id={"addTaskInputDescription"}
            />
            <div className="taskFormButtonBox">
              <button>Submit</button>
            </div>
          </TaskFormContainer>
        );
      }}
    </Formik>
  );
};

const TaskFormContainer = styled.form(() => {
  return {
    ".taskFormButtonBox": {
      display: "flex",
      alignItems: "center",
      justifyContent: "flex-end",
      button: {
        background: "#437db0",
        color: "#fff",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        borderRadius: 4,
        height: 40,
        width: 130,
      },
    },
  };
});

export default TaskForm;
